mapper.delete:

delete from odh_wktm_manage
where USER_NO = #{userNo,jdbcType=CHAR}
  and REC_NO = #{recNo,jdbcType=INTEGER}

mapper.select:

select USER_NO, REC_NO, REG_YMD, WKTM_TYPE, WKTM_SUBTYPE, PROJECT_STAGE, WKTM_START_YMDHM, 
WKTM_END_YMDHM, WKTM_TIMES, MEMO, SUB_FLG, SEPT_FLG, LOGIN_NO, LOGIN_IP, REC_INSERT_TIME, 
DELETE_STATE, UPDATE_NO, UPDATE_IP, REC_UPDATE_TIME
from odh_wktm_manage
where USER_NO = #{userNo,jdbcType=CHAR}
  and REC_NO = #{recNo,jdbcType=INTEGER}
