DROP TABLE IF EXISTS `ODH_MODL_INFO`;
-- CREATE TABLE
CREATE TABLE `OFFICE`.`ODH_MODL_INFO` (
      MODEL_ID                      VARCHAR(10)         NOT NULL                          COMMENT '模块ID',
      PARENT_MODEL_ID               VARCHAR(10)             NULL                          COMMENT '父模块ID',
      MODEL_URL                     VARCHAR(100)            NULL                          COMMENT '模块URL',
      MODEL_NAME                    VARCHAR(26)             NULL                          COMMENT '模块名称',
      MODEL_INFO                    VARCHAR(50)             NULL                          COMMENT '模块说明',
      LAYER_NO                      INT                     NULL                          COMMENT '层数',
      DISPLAY_ORDER                 INT                     NULL                          COMMENT '显示顺序',
      LOGIN_NO                      CHAR(5)                 NULL                          COMMENT '作成者工号',
      LOGIN_IP                      VARCHAR(20)             NULL                          COMMENT '作成者IP',
      REC_INSERT_TIME               DATETIME                NULL                          COMMENT '作成时间',
      UPDATE_NO                     CHAR(5)                 NULL                          COMMENT '更新者工号',
      UPDATE_IP                     VARCHAR(20)             NULL                          COMMENT '更新者IP',
      REC_UPDATE_TIME               DATETIME                NULL                          COMMENT '记录更新时间',
      PRIMARY KEY ( `MODEL_ID`)
)
ENGINE=InnoDB DEFAULT CHARACTER SET = utf8;

